package com.java.loop;

public class DoWhileLoop {

    /**
     * <h2>main</h2>
     * <p>
     * 
     * </p>
     *
     * @param args
     * @return void
     */
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int i = 1;
        do {
            System.out.println(i);
            i++;
        } while (i <= 10);
    }

}
