public class ForEachExample {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int arr[] = { 12, 23, 44, 56, 78 };
        // Printing array using for-each loop
        for (int i : arr) {
            System.out.println(i);
        }
    }

}
